import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import RespartnerListScreen from './RespartnerListScreen';
import RespartnerDetailsScreen from './RespartnerDetailsScreen';

const Stack = createStackNavigator();

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="RespartnerList" component={RespartnerListScreen} />
        <Stack.Screen name="RespartnerDetails" component={RespartnerDetailsScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
export default App;
